contracts/ClaimManage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import "./IBHeroToken.sol";
import "./IAdminTool.sol";
import "./signatureV1Lib.sol";

contract ClaimManage is Initializable, AccessControlUpgradeable, UUPSUpgradeable {
  using SafeMathUpgradeable for uint256;

  bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
  bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
  bytes32 public constant WITHDRAWER_ROLE = keccak256("WITHDRAWER_ROLE");
  address public assetHolder;

  mapping(uint256 => address) public tokenContracts;
  struct UserInfo {
    uint256 totalClaim;
  }
  // address user => type contract(1,2,3,..,n) => user info
  mapping(address => mapping(uint256 => UserInfo)) public claimedInfo;
  mapping(address => mapping(uint256 => bool)) public usedNonces;

  IBHeroToken public bHeroToken;
  IAdminTool public adminTool;

  // voucher info
  mapping(uint256 => VoucherInfo) public voucherInfo;
  mapping(address => mapping(uint256 => uint256)) public userUsedVoucher; //[user][0] = 1/2/3 times used.

  struct VoucherInfo {
    bool isOneTime;
    uint256 maxEachUser;
    uint256 timeEnd;
    uint256 remaining; //reduce when isOneTime = false;
  }

  /// Update code to quick check typeof smc
  /// i.e: whoAmI[1] = 721 => tokenType 1 belong to ERC721
  mapping(uint256 => uint256) public whoAmI;

  event ClaimToken(uint256 tokenType, address indexed user, uint256 reward);
  event ClaimTokens(uint256 tokenType, address indexed user, uint256 amount, uint256 nonce, uint256[] details);
  event BuyHeroViaVoucher(
    uint256 voucherType,
    address indexed user,
    uint256 heroQuantity,
    uint256 amount,
    uint256 nonce
  );

  /// The claim without tokenType
  error ClaimOutOfTokentype(uint256 tokenType);

  function initialize() public initializer {
    __AccessControl_init();
    __UUPSUpgradeable_init();

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(UPGRADER_ROLE, msg.sender);
    _grantRole(MANAGER_ROLE, msg.sender);
    _grantRole(WITHDRAWER_ROLE, msg.sender);
  }

  function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}

  function supportsInterface(bytes4 interfaceId) public view override(AccessControlUpgradeable) returns (bool) {
    return super.supportsInterface(interfaceId);
  }

  function getTotalClaim(address user, uint256 tokenType) public view returns (uint256) {
    return claimedInfo[user][tokenType].totalClaim;
  }

  function setAssetHolder(address _assetHolder) external onlyRole(MANAGER_ROLE) {
    assetHolder = _assetHolder;
  }

  function setAdminTool(address _adminTool) external onlyRole(MANAGER_ROLE) {
    adminTool = IAdminTool(_adminTool);
  }

  function resetUserVoucher(address user, uint256 voucherType) external onlyRole(MANAGER_ROLE) {
    userUsedVoucher[user][voucherType] = 0;
  }

  function setTokenContract(
    uint256 tokenType,
    address _contract,
    uint256 _stdToken
  ) external onlyRole(MANAGER_ROLE) {
    tokenContracts[tokenType] = _contract;
    whoAmI[tokenType] = _stdToken;
  }

  function setVoucherInfo(uint256 voucherType, VoucherInfo memory vInfo) external onlyRole(MANAGER_ROLE) {
    voucherInfo[voucherType] = VoucherInfo(
      vInfo.isOneTime, // vouvher using 1 time or many time.
      vInfo.maxEachUser, // voucher user can use
      vInfo.timeEnd, // end of event
      vInfo.remaining // minus when isOneTime is false.
    );
  }

  function withdraw(address tokenAddress) external onlyRole(WITHDRAWER_ROLE) {
    IERC20Upgradeable token = IERC20Upgradeable(tokenAddress);
    token.transfer(msg.sender, token.balanceOf(address(this)));
  }

  modifier checkVoucherUsed(address user, uint256 voucherType) {
    VoucherInfo memory vc = voucherInfo[voucherType];
    require(block.timestamp <= vc.timeEnd, "Voucher expired");
    require(vc.remaining > 0, "Voucher exceed");
    if (vc.isOneTime) {
      require(userUsedVoucher[user][voucherType] == 0, "Voucher is used!");
    } else {
      require(userUsedVoucher[user][voucherType] < vc.maxEachUser, "Voucher exceed for user");
    }
    _;
  }

  function claimToken(uint256 tokenType, uint256 amount) internal {
    UserInfo storage userClaimInfo = claimedInfo[msg.sender][tokenType];
    uint256 reward;
    // Checking typeof token
    if (whoAmI[tokenType] == 20) {
      require(userClaimInfo.totalClaim < amount, "User exceed balance");
      reward = amount.sub(userClaimInfo.totalClaim);
      userClaimInfo.totalClaim = amount;
      adminTool.transferSingleToken(tokenContracts[tokenType], msg.sender, reward);
    } else if (whoAmI[tokenType] == 721) {
      uint256 formatAmount = amount.div(10**18);
      require(userClaimInfo.totalClaim < formatAmount, "User exceed balance");
      reward = formatAmount.sub(userClaimInfo.totalClaim);
      userClaimInfo.totalClaim = formatAmount;
      IBHeroToken(tokenContracts[tokenType]).claimHero(reward, msg.sender);
    } else {
      revert ClaimOutOfTokentype(tokenType);
    }

    emit ClaimToken(tokenType, msg.sender, reward);
  }

  function claimTokens(
    uint256 tokenType,
    uint256 amount,
    uint256 nonce,
    uint256[] calldata details,
    bytes memory signature
  ) public {
    require(tokenContracts[tokenType] != address(0), "Contract not existed");
    require(!usedNonces[msg.sender][nonce], "Nonce has used");
    usedNonces[msg.sender][nonce] = true;

    UserInfo storage userClaimInfo = claimedInfo[msg.sender][tokenType];
    bytes memory listDetails = concate(details);
    bytes32 rawMessage = keccak256(
      abi.encodePacked(msg.sender, tokenType, amount, nonce, listDetails, userClaimInfo.totalClaim, address(this)) // remove total claim to test
    );
    bytes32 message = getEncodeMessage(rawMessage);
    require(_verify(message, signature), "Message isn't correct");

    uint256 tokenTypeT = tokenType; // Fix stack too deep

    if (details.length == 0) {
      claimToken(tokenTypeT, amount); // ERC20
    } else {
      uint256 numNormalHeros;
      for (uint256 i = 0; i < details.length; ++i) {
        if (details[i] == 0) {
          numNormalHeros++;
          continue;
        }
        uint256 count = claimRequestToken(details[i], tokenTypeT);
        userClaimInfo.totalClaim += count;
      }
      if (numNormalHeros > 0) {
        claimToken(tokenTypeT, (userClaimInfo.totalClaim + numNormalHeros) * 1 ether);
      }
      uint256 formatAmount = amount / 1 ether;
      require(userClaimInfo.totalClaim == formatAmount, "Claimed invalid");
    }
    emit ClaimTokens(tokenType, msg.sender, amount, nonce, details);
  }

  function claimRequestToken(uint256 detail, uint256 tokenType) internal returns (uint256) {
    uint256 count;
    uint256 rarity;
    uint256 targetBlock;
    uint256 tokenRequestDetails;

    (count, rarity, targetBlock, tokenRequestDetails) = decodeLocalDetails(detail, tokenType);
    IBHeroToken(tokenContracts[tokenType]).createTokenRequest(
      msg.sender,
      count,
      rarity,
      targetBlock,
      tokenRequestDetails
    );
    return count;
  }

  function decodeLocalDetails(uint256 details, uint256 tokenType)
    internal
    view
    returns (
      uint256 count,
      uint256 rarity,
      uint256 targetBlock,
      uint256 tokenRequestDetails
    )
  {
    uint256 size = IBHeroToken(tokenContracts[tokenType]).getTotalHeroByUser(msg.sender);
    uint256 limit = IBHeroToken(tokenContracts[tokenType]).design().getTokenLimit();
    require(size < limit, "User limit reached");

    count = details & (2**10 - 1);
    rarity = (details >> 10) & (2**5 - 1);
    targetBlock = block.number + 5;

    // category: 5 bits.
    // isHeroS: 5 bits.
    // dropLength: 5 bits.
    // max 6 drops: 15 * 6 = 90 bits.
    // skin: 5 bits.
    uint256 moreDetails = (details >> 15) & ((uint256(1) << 110) - 1);

    tokenRequestDetails = targetBlock | (moreDetails << 30);
  }

  function concate(uint256[] calldata data) internal pure returns (bytes memory) {
    if (data.length == 0) {
      return "";
    }
    bytes memory result;
    for (uint256 i = 0; i < data.length; ++i) {
      result = abi.encodePacked(result, StringsUpgradeable.toString(data[i]));
    }
    return (result);
  }

  function buyHeroUseVoucher(
    address tokenPay,
    uint256 voucherType,
    uint256 heroQuantity,
    uint256 amount,
    uint256 nonce,
    bytes memory signature
  ) public checkVoucherUsed(msg.sender, voucherType) {
    address buyer = msg.sender; // avoid read storage many-time
    require(!usedNonces[buyer][nonce], "Nonce has used");
    usedNonces[buyer][nonce] = true;

    // verify signature
    bytes32 rawMessage = keccak256(
      abi.encodePacked(buyer, tokenPay, voucherType, heroQuantity, amount, nonce, address(this))
    );
    bytes32 message = getEncodeMessage(rawMessage);
    require(_verify(message, signature), "Message isn't correct");

    // logic
    doAfterBuyViaVoucher(voucherType, buyer); // TODO: check re-entrace
    IERC20Upgradeable(tokenPay).transferFrom(buyer, address(this), amount); // remember approve for this contract use money
    IBHeroToken(tokenContracts[1]).claimHero(heroQuantity, buyer); // tokenContracts[1]: is BHero
    emit BuyHeroViaVoucher(voucherType, buyer, heroQuantity, amount, nonce);
  }

  function doAfterBuyViaVoucher(uint256 voucherType, address user) internal {
    userUsedVoucher[user][voucherType] += 1;
    VoucherInfo storage vc = voucherInfo[voucherType];
    if (!vc.isOneTime) {
      vc.remaining -= 1;
    }
  }

  function getEncodeMessage(bytes32 rawMessage) internal pure returns (bytes32 message) {
    bytes memory s = abi.encodePacked(rawMessage);
    message = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n", StringsUpgradeable.toString(s.length), s));
  }

  function _verify(bytes32 message, bytes memory signature) public view returns (bool) {
    address signer = signtureERC721.checkMessageSignature(message, signature);
    return hasRole(DEFAULT_ADMIN_ROLE, signer);
  }

  // client request to check user can use voucher or not?
  function userCanUseVoucher(uint256 voucherType, address user) public view returns (bool) {
    VoucherInfo memory vc = voucherInfo[voucherType];
    if (vc.isOneTime) {
      return userUsedVoucher[user][voucherType] == 0;
    } else {
      return
        (userUsedVoucher[user][voucherType] < vc.maxEachUser) && (vc.remaining > 0) && (vc.timeEnd >= block.timestamp);
    }
  }
}

