contracts/signatureV1Lib.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library signtureERC721 {
  function splitSignature(bytes memory sig)
    internal
    pure
    returns (
      uint8 v,
      bytes32 r,
      bytes32 s
    )
  {
    require(sig.length == 65, "Signature isn't validate");
    assembly {
      // first 32 bytes, after the length prefix.
      r := mload(add(sig, 32))
      // second 32 bytes.
      s := mload(add(sig, 64))
      // final byte (first byte of the next 32 bytes).
      v := byte(0, mload(add(sig, 96)))
    }
    return (v, r, s);
  }

  function checkMessageSignature(bytes32 message, bytes memory signature) internal pure returns (address) {
    (uint8 v, bytes32 r, bytes32 s) = splitSignature(signature);
    address signer = ecrecover(message, v, r, s);
    return signer;
  }
}

